$(document).ready(function(){var a=20;if($("#topMenu").html()===null){a=0}if(location.href.indexOf("#rule_")!==-1){var b=location.href.split("#rule_")[1];$("html, body").scrollTop($("#rule_"+b).offset().top-a)}$('a[href*="#rule_"]').click(function(){var b=$(window).scrollTop();var c=$(this).attr("href").split("#rule_")[1];location.hash="rule_"+c;$("html, body").scrollTop(b);$("html, body").animate({scrollTop:$("#rule_"+c).offset().top-a});return false})})